mapper.select:
select d.id,d.name, d.category_id,d.price,d.image, d.description,d.status, d.create_time, d.update_time, d.create_user, d.update_user, c.name as categoryName from dish d left join category c on d.category_id = c.id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}
